function Murica_Road () {
    Music = 1
    Weapon = 0
    scroller.setLayerImage(scroller.BackgroundLayer.Layer0, assets.image`Background Level 2`)
    tiles.setCurrentTilemap(tilemap`Murica Road`)
    mySprite = sprites.create(assets.image`Felix 10`, SpriteKind.Player)
    mySprite4 = sprites.create(assets.image`Jack 4`, SpriteKind.Jack_)
    mySprite2 = sprites.create(assets.image`Wagon`, SpriteKind.Wagon)
    scene.cameraFollowSprite(mySprite)
    tiles.placeOnTile(mySprite, tiles.getTileLocation(11, 14))
    tiles.placeOnTile(mySprite4, tiles.getTileLocation(15, 14))
    tiles.placeOnTile(mySprite2, tiles.getTileLocation(14, 17))
    color.startFadeFromCurrent(color.originalPalette, 1000)
    pause(1500)
    if (_2ndScene1stTime == 0) {
        story.printCharacterText("It seems that we've gotten far, Jack.", "Felix")
        story.printCharacterText("Good. I really can't wait to finally finish the trail.", "Jack")
        story.printCharacterText("I know it's been a dream of yours Jack. I'm sure we'll get there.", "Felix")
    }
    mySprite4.follow(mySprite2, 50)
    pause(1000)
    if (_2ndScene1stTime == 0) {
        mySprite.setImage(assets.image`Felix 9`)
        pause(1000)
        Title2 = sprites.create(assets.image`CinemaBars 2`, SpriteKind.Nothing)
        Title2.setStayInScreen(true)
        tileUtil.centerCameraOnTile(tiles.getTileLocation(3, 7))
        pause(1000)
        mySprite3 = sprites.create(assets.image`Zombie 13`, SpriteKind.First_Box)
        tiles.placeOnTile(mySprite3, tiles.getTileLocation(3, 7))
        animation.runImageAnimation(
        mySprite3,
        assets.animation`First Box Spawn`,
        200,
        false
        )
        pause(1600)
        pause(500)
        story.printCharacterText("What!!!", "Felix")
        scene.cameraFollowSprite(mySprite)
        sprites.destroy(Title2)
        Title2 = sprites.create(assets.image`CinemaBars`, SpriteKind.Nothing)
        Title2.setStayInScreen(true)
        pause(500)
        story.printCharacterText("What's it this time?", "Jack")
        story.printCharacterText("It be a box, I guess.", "Felix")
        animation.runImageAnimation(
        Title2,
        assets.animation`Action Time`,
        400,
        false
        )
        story.printCharacterText("Go ahead and bust it open fella!", "Jack")
        pause(1000)
        controller.moveSprite(mySprite, 75, 75)
        Moving = true
        statusbar = statusbars.create(60, 6, StatusBarKind.Health)
        statusbar.setColor(2, 15)
        statusbar.positionDirection(CollisionDirection.Top)
        statusbar2 = statusbars.create(60, 6, StatusBarKind.None)
        statusbar2.positionDirection(CollisionDirection.Bottom)
        statusbar2.setColor(12, 15)
        Ammo = 6
        sprites.destroy(Title2)
        Notification.notify("Pick up the box", 4, assets.image`Box Icon`)
        _2ndScene1stTime = 1
    } else {
        story.printCharacterText("Zombies Incoming!", "Felix")
        Moving = true
        statusbar = statusbars.create(60, 6, StatusBarKind.Health)
        statusbar.setColor(2, 15)
        statusbar.positionDirection(CollisionDirection.Top)
        statusbar2 = statusbars.create(60, 6, StatusBarKind.None)
        statusbar2.positionDirection(CollisionDirection.Bottom)
        statusbar2.setColor(12, 15)
        Ammo = 6
        Shooting = true
        controller.moveSprite(mySprite, 0, 0)
        Title2 = sprites.create(assets.image`Fight 2`, SpriteKind.Title)
        Title2.setFlag(SpriteFlag.GhostThroughWalls, true)
        Title2.setStayInScreen(true)
        Title2.setPosition(scene.cameraProperty(CameraProperty.X), scene.cameraProperty(CameraProperty.Y))
        scene.cameraShake(4, 300)
        Title2.lifespan = 1000
        animation.runImageAnimation(
        Title2,
        assets.animation`Fight 9`,
        100,
        false
        )
        pause(1000)
        controller.moveSprite(mySprite, 75, 75)
        Zombies_Left = 48
        timer.background(function () {
            Box_Spawning = 1
            for (let index = 0; index < 48; index++) {
                if (Level == 1.2) {
                    pause(1000)
                    timer.background(function () {
                        mySprite3 = sprites.create(assets.image`Box`, SpriteKind.Nothing)
                        tiles.placeOnRandomTile(mySprite3, assets.tile`Grass`)
                        animation.runImageAnimation(
                        mySprite3,
                        assets.animation`Zombie Spawn 3`,
                        100,
                        false
                        )
                        timer.after(800, function () {
                            mySprite3.setKind(SpriteKind.Zombie)
                            mySprite3.follow(mySprite, 25)
                            Zombie_Look()
                        })
                    })
                }
            }
        })
    }
}
