    if (Ammo == 0) {
        sprites.destroy(statusbar3)
        Ammo = 6
        Shooting = true
    }
    extraEffects.createSpreadEffectOnAnchor(otherSprite, extraEffects.createSingleColorSpreadEffectData(14, ExtraEffectPresetShape.Explosion), 200, 48)
    Box_Value = randint(1, 8)
    if (Box_Value == 1) {
        Ammo = 6
        Weapon = 1
        sprites.destroy(otherSprite)
        mySprite.sayText("RIFLE!", 1000, true)
    } else {
        if (Box_Value == 2) {
            Ammo = 6
            Weapon = 2
            sprites.destroy(otherSprite)
            mySprite.sayText("SHOTGUN!", 1000, true)
        } else {
            if (Box_Value == 3) {
                Ammo = 33
                Weapon = 3
                sprites.destroy(otherSprite)
                mySprite.sayText("S.M.G!", 1000, true)
            } else {
                if (Box_Value == 4) {
                    HealApple = 100
                    sprites.destroy(otherSprite)
                    mySprite.sayText("APPLE!", 1500, true)
                } else {
                    if (Box_Value == 5) {
                        Weapon = 4
                        Ammo = 3
                        sprites.destroy(otherSprite)
                        mySprite.sayText("ROCKET LAUNCHER!", 1500, true)
                    } else {
                        if (Box_Value == 6) {
                            HotlineCoffee = 400
                            sprites.destroy(otherSprite)
                            mySprite.sayText("COFFEE!", 1500, true)
                        } else {
                            if (Box_Value == 7) {
                                sprites.destroy(otherSprite)
                                mySprite.sayText("EMPTY...", 1500, true)
                            }
                        }
                    }
                }
            }
        }
    }
